from flask import Flask, render_templateimport requestsimport pandas as pdfrom matplotlib.figure import Figureimport matplotlib.pyplot as pltfrom matplotlib.backends.backend_agg import FigureCanvasAgg as FigureCanvas# Klasa Site Utils# Będzie zawierała dodatkowe# machanizmy wykorzystywane w naszej aplikacjiclass SiteUtils():    # Zapytaj API koronawirusowe o aktywne przypadki w Polsce    def request_active_covid_cases(self):        zakazenia = requests.get("https://api.covid19api.com/country/poland")        return zakazenia    # Stwórz z pozyskanych danych DataFrame    def prepare_data(self):        zakazenia = self.request_active_covid_cases()        df = pd.read_json(zakazenia.content)        return df    # Stwórz wykres z pozyskanych danych    def create_figure(self):        df=self.prepare_data()        plot=df['Active'].plot()        fig=plot.get_figure()        return figapp = Flask(__name__)utils=SiteUtils()@app.route('/')@app.route('/index.html')def index():    return render_template('index.html')@app.route('/plot.png')def plot_png():    fig=utils.create_figure()    output=io.BytesIO()    FigureCanvas(fig).print_png(output)    return Response(output.getvalue(), mimetype='image/png')    if __name__=="__main__":    app.run()